+import express from 'express';
+import helmet from 'helmet';
+import cors from 'cors';
+import compression from 'compression';
+import rate_limit from 'express-rate-limit';
+import nodemailer from 'nodemailer';
+import dotenv from 'dotenv';
+import { z } from 'zod';
+
+dotenv.config();
+
+const app = express();
+
+// Environment helpers
+const get_env = (key: string, fallback?: string): string => {
+    const value = process.env[key];
+    if (value && value.length > 0) return value;
+    if (fallback !== undefined) return fallback;
+    throw new Error(`Missing required env: ${key}`);
+};
+
+const parse_int = (value: string | undefined, fallback: number): number => {
+    const n = value ? parseInt(value, 10) : NaN;
+    return Number.isFinite(n) ? n : fallback;
+};
+
+// Security & middleware
+app.use(helmet());
+app.use(compression());
+app.use(express.json({ limit: '100kb' }));
+
+const allowed_origin = get_env('FRONTEND_ORIGIN', '*');
+app.use(
+    cors({
+        origin: allowed_origin === '*' ? true : allowed_origin,
+        methods: ['POST', 'OPTIONS'],
+        credentials: false,
+    })
+);
+
+// Rate limit for contact endpoint
+const submissions_per_window = parse_int(process.env.FORM_RATE_LIMIT_MAX, 5);
+const window_ms = parse_int(process.env.FORM_RATE_LIMIT_WINDOW_MS, 15 * 60 * 1000);
+const limiter = rate_limit({
+    windowMs: window_ms,
+    max: submissions_per_window,
+    standardHeaders: true,
+    legacyHeaders: false,
+});
+
+// Validation schema
+const contact_schema = z.object({
+    name: z.string().min(2).max(100),
+    email: z.string().email().max(200),
+    phone: z.string().max(50).optional().or(z.literal('')).optional(),
+    message: z.string().min(10).max(5000),
+    recaptchaToken: z.string().min(10),
+});
+
+const sanitize_line = (s: string): string => s.replace(/[\r\n\t]+/g, ' ').trim();
+
+async function verify_recaptcha(token: string): Promise<{ ok: boolean; score: number }> {
+    const secret = get_env('RECAPTCHA_SECRET');
+    const min_score = Number(process.env.RECAPTCHA_MIN_SCORE ?? '0.5');
+    const params = new URLSearchParams();
+    params.set('secret', secret);
+    params.set('response', token);
+
+    const res = await fetch('https://www.google.com/recaptcha/api/siteverify', {
+        method: 'POST',
+        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
+        body: params.toString(),
+    });
+    if (!res.ok) return { ok: false, score: 0 };
+    const data: any = await res.json();
+    const score = typeof data.score === 'number' ? data.score : 0;
+    const ok = data.success === true && score >= min_score;
+    return { ok, score };
+}
+
+function create_transporter() {
+    const host = get_env('SMTP_HOST', 'smtp.strato.de');
+    const port = parse_int(process.env.SMTP_PORT, 465);
+    const secure = port === 465; // SSL
+    const user = get_env('SMTP_USER');
+    const pass = get_env('SMTP_PASS');
+
+    return nodemailer.createTransport({
+        host,
+        port,
+        secure,
+        auth: { user, pass },
+    });
+}
+
+app.post('/api/contact', limiter, async (req, res) => {
+    try {
+        const parsed = contact_schema.safeParse(req.body);
+        if (!parsed.success) {
+            return res.status(400).json({ ok: false, error: 'invalid_payload' });
+        }
+        const { name, email, phone, message, recaptchaToken } = parsed.data;
+
+        const verification = await verify_recaptcha(recaptchaToken);
+        if (!verification.ok) {
+            return res.status(400).json({ ok: false, error: 'recaptcha_failed', score: verification.score });
+        }
+
+        const transporter = create_transporter();
+
+        const mail_to = get_env('MAIL_TO');
+        const from_user = get_env('SMTP_USER');
+        const subject = 'Neue Kontaktanfrage (PADERCLEAN)';
+        const safe_name = sanitize_line(name);
+        const safe_email = sanitize_line(email);
+        const safe_phone = sanitize_line(String(phone ?? ''));
+        const safe_message = message.trim().slice(0, 5000);
+
+        await transporter.sendMail({
+            from: `PADERCLEAN Website <${from_user}>`,
+            to: mail_to,
+            replyTo: safe_email,
+            subject,
+            text: `Name: ${safe_name}\nE-Mail: ${safe_email}\nTelefon: ${safe_phone}\n\nNachricht:\n${safe_message}\n`,
+        });
+
+        return res.json({ ok: true, message: 'sent' });
+    } catch (err) {
+        return res.status(500).json({ ok: false, error: 'server_error' });
+    }
+});
+
+// Health endpoint
+app.get('/api/health', (_req, res) => {
+    res.json({ ok: true });
+});
+
+const port = parse_int(process.env.PORT, 3000);
+app.listen(port, () => {
+    // eslint-disable-next-line no-console
+    console.log(`API listening on :${port}`);
+});
+
*** End Patch"]}

