export class CookieConsentComponent {
  visible = signal(false);
  constructor() {
    const consent = localStorage.getItem('pc_cookie_consent');
    if (!consent) this.visible.set(true);
    window.addEventListener('pc-open-cookie', () => this.visible.set(true));
  }
  acceptAll() {
    localStorage.setItem('pc_cookie_consent', JSON.stringify({ analytics: true, ts: Date.now() }));
    this.visible.set(false);
  }
  denyAll() {
    localStorage.setItem('pc_cookie_consent', JSON.stringify({ analytics: false, ts: Date.now() }));
    this.visible.set(false);
  }
}

