{"output":"import { Injectable, signal } from '@angular/core';

export interface Toast {
  id: number;
  type: 'success' | 'error' | 'info';
  message: string;
  timeout?: number;
}

@Injectable({ providedIn: 'root' })
export class ToastService {
  private seq = 1;
  toasts = signal<Toast[]>([]);

  show(type: Toast['type'], message: string, timeout = 3500) {
    const id = this.seq++;
    const t: Toast = { id, type, message, timeout };
    this.toasts.update(list => [...list, t]);
    if (timeout) setTimeout(() => this.dismiss(id), timeout);
    return id;
  }
  success(m: string, timeout?: number) { return this.show('success', m, timeout); }
  error(m: string, timeout?: number) { return this.show('error', m, timeout); }
  info(m: string, timeout?: number) { return this.show('info', m, timeout); }
  dismiss(id: number) { this.toasts.update(list => list.filter(t => t.id !== id)); }
  clear() { this.toasts.set([]); }
}

