{"output":"import { Injectable, Inject, Renderer2, RendererFactory2 } from '@angular/core';
import { Title, Meta, DOCUMENT } from '@angular/platform-browser';

export interface SeoOptions {
  title: string;
  description: string;
  imageUrl?: string;
  url?: string;
  type?: string; // og:type, default website
}

@Injectable({ providedIn: 'root' })
export class SeoService {
  private r: Renderer2;
  constructor(private title: Title, private meta: Meta, rf: RendererFactory2, @Inject(DOCUMENT) private doc: Document) {
    this.r = rf.createRenderer(null, null);
  }

  set(opts: SeoOptions) {
    const { title, description, imageUrl, url, type } = opts;
    this.title.setTitle(title);
    this.meta.updateTag({ name: 'description', content: description });
    this.meta.updateTag({ property: 'og:title', content: title } as any);
    this.meta.updateTag({ property: 'og:description', content: description } as any);
    this.meta.updateTag({ property: 'og:type', content: type || 'website' } as any);
    if (url) this.meta.updateTag({ property: 'og:url', content: url } as any);
    if (imageUrl) {
      this.meta.updateTag({ property: 'og:image', content: imageUrl } as any);
      this.meta.updateTag({ name: 'twitter:image', content: imageUrl });
    }
    this.meta.updateTag({ name: 'twitter:card', content: 'summary_large_image' });
    this.meta.updateTag({ name: 'twitter:title', content: title });
    this.meta.updateTag({ name: 'twitter:description', content: description });
  }

  addJsonLd(obj: unknown) {
    const script = this.r.createElement('script');
    script.type = 'application/ld+json';
    script.text = JSON.stringify(obj);
    this.r.appendChild(this.doc.head, script);
  }
}

