{"output":"import { Injectable } from '@angular/core';
import { environment } from '../../../environments/environment';

declare const grecaptcha: any;

@Injectable({ providedIn: 'root' })
export class RecaptchaService {
  private loaded = false;
  private loadingPromise?: Promise<void>;

  private loadScript(): Promise<void> {
    if (this.loaded) return Promise.resolve();
    if (this.loadingPromise) return this.loadingPromise;

    this.loadingPromise = new Promise<void>((resolve, reject) => {
      const key = environment.recaptchaSiteKey;
      if (!key) {
        // No key configured; resolve to allow dev without reCAPTCHA
        this.loaded = true; resolve(); return;
      }
      const script = document.createElement('script');
      script.src = `https://www.google.com/recaptcha/api.js?render=${encodeURIComponent(key)}`;
      script.async = true; script.defer = true;
      script.onload = () => { this.loaded = true; resolve(); };
      script.onerror = () => reject(new Error('Failed to load reCAPTCHA script'));
      document.head.appendChild(script);
    });
    return this.loadingPromise;
  }

  async getToken(action: string): Promise<string> {
    await this.loadScript();
    const key = environment.recaptchaSiteKey;
    if (!key || !(window as any).grecaptcha) return '';
    return new Promise<string>((resolve, reject) => {
      try {
        grecaptcha.ready(() => {
          grecaptcha.execute(key, { action }).then(resolve).catch(() => resolve(''));
        });
      } catch {
        resolve('');
      }
    });
  }
}

