{"output":"import { Injectable, Inject } from '@angular/core';
import { HttpErrorResponse, HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { environment } from '../../../environments/environment';
import { ToastService } from '../components/toast.service';
import { DOCUMENT } from '@angular/common';

@Injectable()
export class ApiInterceptor implements HttpInterceptor {
  constructor(@Inject(DOCUMENT) private doc: Document, private toast: ToastService) {}

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    let request = req;

    // Attach CSRF token (optional via env)
    const csrfHeader = (environment as any).csrfHeaderName as string | undefined;
    const csrfCookie = (environment as any).csrfCookieName as string | undefined;
    if (csrfHeader && csrfCookie) {
      const token = this.readCookie(csrfCookie);
      if (token) {
        request = request.clone({ setHeaders: { [csrfHeader]: token } });
      }
    }

    // Attach API token from localStorage (optional via env)
    const apiHeader = (environment as any).apiTokenHeader as string | undefined;
    const apiKey = (environment as any).apiTokenKey as string | undefined;
    if (apiHeader && apiKey) {
      const value = this.readStorage(apiKey);
      if (value) {
        request = request.clone({ setHeaders: { [apiHeader]: value } });
      }
    }

    return next.handle(request).pipe(
      catchError((err: HttpErrorResponse) => {
        // Map to user-friendly messages (optional UI can subscribe)
        if (err.status === 0) {
          this.toast.error('Netzwerkfehler. Bitte Verbindung prǬfen.');
          return throwError(() => new Error('Netzwerkfehler. Bitte prǬfen Sie Ihre Verbindung.'));
        }
        if (err.status >= 500) {
          this.toast.error('Serverfehler. Bitte sp��ter erneut versuchen.');
          return throwError(() => new Error('Serverfehler. Bitte sp��ter erneut versuchen.'));
        }
        if (err.status === 400) {
          this.toast.error('UngǬltige Eingaben. Bitte Formular prǬfen.');
          return throwError(() => new Error('UngǬltige Eingaben. Bitte prǬfen Sie das Formular.'));
        }
        this.toast.error('Fehler aufgetreten. Bitte sp��ter erneut versuchen.');
        return throwError(() => err);
      })
    );
  }

  private readCookie(name: string): string | null {
    const cookies = this.doc?.cookie?.split(';') || [];
    for (const c of cookies) {
      const [k, v] = c.trim().split('=');
      if (k === name) return decodeURIComponent(v || '');
    }
    return null;
  }

  private readStorage(key: string): string | null {
    try { return localStorage.getItem(key); } catch { return null; }
  }
}
