{"output":"import { ChangeDetectionStrategy, Component } from '@angular/core';
import { firstValueFrom } from 'rxjs';
import { SeoService } from '../../services/seo.service';
import { CommonModule } from '@angular/common';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { ContactService } from '../../services/contact.service';
import { ToastService } from '../../components/toast.service';
import { RecaptchaService } from '../../services/recaptcha.service';

@Component({
  standalone: true,
  selector: 'pc-kontakt',
  imports: [ReactiveFormsModule, CommonModule],
  templateUrl: './kontakt.component.html',
  styleUrls: ['./kontakt.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class KontaktComponent {
  form = new FormGroup({
    name: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.minLength(2)] }),
    email: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.email] }),
    phone: new FormControl('', { nonNullable: true }),
    message: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.minLength(10)] }),
  });

  submitting = false;
  success?: boolean;

  constructor(
    seo: SeoService,
    private contact: ContactService,
    private toast: ToastService,
    private rc: RecaptchaService
  ) {
    seo.set({
      title: 'Kontakt - Termin anfragen | PADERCLEAN',
      description: 'Termin anfragen oder Fragen stellen - Kontaktformular, Telefon, E-Mail und Standort in Paderborn.'
    });
  }

  async submit() {
    if (this.form.invalid) return;
    this.submitting = true;
    try {
      const token = await this.rc.getToken('contact');
      const payload = { ...this.form.getRawValue(), recaptchaToken: token } as any;
      await firstValueFrom(this.contact.submitContact(payload));
      this.success = true;
      this.toast.success('Danke fǬr Ihre Anfrage! Wir melden uns in KǬrze.');
      this.form.reset();
    } catch (e) {
      this.success = false;
      this.toast.error('Fehler: Nachricht konnte nicht gesendet werden.');
    } finally {
      this.submitting = false;
    }
  }
}
