{"output":"import { ChangeDetectionStrategy, Component, ElementRef, Input, OnDestroy, OnInit, signal } from '@angular/core';

@Component({
  selector: 'pc-counter',
  standalone: true,
  template: `
  <div class="counter">
    <div class="counter__value">{{display()}}</div>
    <div class="counter__label"><ng-content></ng-content></div>
  </div>`,
  styleUrls: ['./counter.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class CounterComponent implements OnInit, OnDestroy {
  @Input() to = 100;
  @Input() duration = 1200;
  @Input() prefix = '';
  @Input() suffix = '';
  private raf?: number;
  private start = 0;
  display = signal('0');

  constructor(private el: ElementRef<HTMLElement>) {}

  ngOnInit(): void {
    const startAnim = (ts: number) => {
      if (!this.start) this.start = ts;
      const p = Math.min(1, (ts - this.start) / this.duration);
      const val = Math.round(this.to * (p * (2 - p)));
      this.display.set(`${this.prefix}${val.toLocaleString()}${this.suffix}`);
      if (p < 1) this.raf = requestAnimationFrame(startAnim);
    };
    const io = new IntersectionObserver((entries) => {
      if (entries.some(e => e.isIntersecting)) {
        this.raf = requestAnimationFrame(startAnim);
        io.disconnect();
      }
    }, { threshold: 0.3 });
    io.observe(this.el.nativeElement);
  }

  ngOnDestroy(): void { if (this.raf) cancelAnimationFrame(this.raf); }
}

