  margin-inline: auto;
}

.btn {
  display: inline-flex;
  align-items: center;
  gap: .5rem;
  background: var(--pc-blue);
  color: #fff;
  border: 0;
  border-radius: 999px;
  padding: .85rem 1.25rem;
  font-weight: 600;
  box-shadow: var(--shadow-sm);
  transition: transform .2s ease, box-shadow .2s ease, background .2s ease;
}
