.site-header__links a { margin-left: 1rem; font-weight: 600; color: var(--pc-text); }
.site-header__links a.active { color: var(--pc-blue); }

/* Footer */
.site-footer { padding: 2rem 0; color: var(--pc-text-light); background: #0c1d3608; border-top: 1px solid #eef3f7; }
.site-footer__grid { display: grid; gap: 1rem; grid-template-columns: 1fr; }
@media (min-width: 800px) { .site-footer__grid { grid-template-columns: 2fr 1fr 1fr; } }

/* Hero */
.hero {
  position: relative; padding: 5rem 0 3rem; overflow: hidden; color: #fff;
  background: radial-gradient(1800px 600px at 80% -10%, #3a6ea5 10%, transparent 60%) , linear-gradient(120deg, #0c1d36 0%, #112a4f 100%);
}
.hero__inner { display: grid; gap: 2rem; align-items: center; }
