/* Pricing */
.pricing { display: grid; gap: 1.25rem; grid-template-columns: repeat(auto-fit,minmax(240px,1fr)); }
.price-card { background: #fff; border-radius: var(--radius); padding: 1.25rem; box-shadow: var(--shadow-sm); border: 1px solid #e7eef4; }
.price-card__title { font-weight: 700; }
.price-card__price { font-weight: 700; color: var(--pc-blue); font-size: 1.8rem; }

/* Before/After */
.ba { position: relative; border-radius: var(--radius); overflow: hidden; box-shadow: var(--shadow-sm); }
