+import { Injectable, Inject, Renderer2, RendererFactory2, effect, signal } from '@angular/core';
+import { DOCUMENT } from '@angular/common';
+import { environment } from '../../environments/environment';
+
+declare global { interface Window { dataLayer?: any[]; gtag?: (...args: any[]) => void } }
+
+@Injectable({ providedIn: 'root' })
+export class AnalyticsService {
+  private r: Renderer2;
+  private enabled = signal(false);
+  constructor(rf: RendererFactory2, @Inject(DOCUMENT) private doc: Document) {
+    this.r = rf.createRenderer(null, null);
+    effect(() => {
+      if (this.enabled()) this.ensureGtag();
+    });
+  }
+
+  init() {
+    const id = (environment as any).analyticsMeasurementId as string | undefined;
+    if (!id) return; // no analytics configured
+    // check cookie consent
+    try {
+      const raw = localStorage.getItem('pc_cookie_consent');
+      const consent = raw ? JSON.parse(raw) : null;
+      if (consent?.analytics === true) this.enabled.set(true);
+    } catch { /* ignore */ }
+    // listen for settings reopening; user may toggle and reload later
+    this.doc.defaultView?.addEventListener('storage', () => {
+      const raw = localStorage.getItem('pc_cookie_consent');
+      try { const c = raw ? JSON.parse(raw) : null; this.enabled.set(!!c?.analytics); } catch {}
+    });
+  }
+
+  private ensureGtag() {
+    const id = (environment as any).analyticsMeasurementId as string | undefined;
+    if (!id) return;
+    if (this.doc.getElementById('ga-gtag')) return;
+    const s = this.r.createElement('script');
+    s.async = true; s.id = 'ga-gtag';
+    s.src = `https://www.googletagmanager.com/gtag/js?id=${encodeURIComponent(id)}`;
+    this.r.appendChild(this.doc.head, s);
+    (this.doc.defaultView as any).dataLayer = (this.doc.defaultView as any).dataLayer || [];
+    function gtag(){ (this as any).dataLayer.push(arguments); }
+    (this.doc.defaultView as any).gtag = gtag;
+    (this.doc.defaultView as any).gtag('js', new Date());
+    (this.doc.defaultView as any).gtag('config', id);
+  }
+}
+
*** End Patch"]}

