.reveal--visible { opacity: 1; transform: translateY(0); }

/* Header */
.site-header { position: sticky; top: 0; z-index: 10; background: rgba(255,255,255,.8); backdrop-filter: blur(8px); border-bottom: 1px solid #eef3f7; }
