  changeDetection: ChangeDetectionStrategy.OnPush
})
export class KontaktComponent {
  form = new FormGroup({
    name: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.minLength(2)] }),
    email: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.email] }),
    phone: new FormControl('', { nonNullable: true }),
    message: new FormControl('', { nonNullable: true, validators: [Validators.required, Validators.minLength(10)] }),
  });

  submitting = false;
  success?: boolean;

  constructor(
    seo: SeoService,
    private contact: ContactService,
    private toast: ToastService,
    private rc: RecaptchaService
  ) {
    seo.set({
      title: 'Kontakt - Termin anfragen | PADERCLEAN',
      description: 'Termin anfragen oder Fragen stellen - Kontaktformular, Telefon, E-Mail und Standort in Paderborn.'
    });
  }

  async submit() {
    if (this.form.invalid) return;
    this.submitting = true;
    try {
      const token = await this.rc.getToken('contact');
      const payload = { ...this.form.getRawValue(), recaptchaToken: token } as any;
      await this.contact.submitContact(payload).toPromise();
      this.success = true;
      this.toast.success('Danke f�r Ihre Anfrage! Wir melden uns in K�rze.');
      this.form.reset();
    } catch (e) {
      this.success = false;
      this.toast.error('Fehler: Nachricht konnte nicht gesendet werden.');
    } finally {
      this.submitting = false;
    }
  }
}

