.page-title::after { content: ''; position: absolute; left: 0; right: 0; bottom: -6px; height: 8px; background: 
linear-gradient(90deg, #d6e8f0, transparent); border-radius: 6px; }

.btn {
  display: inline-flex;
  align-items: center;
  gap: .5rem;
  background: var(--pc-blue);
  color: #fff;
  border: 0;
  border-radius: 999px;
  padding: .85rem 1.25rem;
  font-weight: 600;
  box-shadow: var(--shadow-sm);
  transition: transform .2s ease, box-shadow .2s ease, background .2s ease;
}
.btn:hover { transform: translateY(-1px); box-shadow: var(--shadow-md); }
.btn:active { transform: translateY(0); }
.btn--ghost { background: transparent; color: var(--pc-dark); outline: 2px solid var(--pc-blue); }

.card {
  background: #fff;
  border-radius: var(--radius);
  box-shadow: var(--shadow-sm);
  padding: 1.25rem;
}

/* Reveal on scroll */
.reveal { opacity: 0; transform: translateY(16px); transition: opacity var(--motion-duration) var(--motion-ease), 
transform var(--motion-duration) var(--motion-ease); }
.reveal--visible { opacity: 1; transform: translateY(0); }

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  * { animation-duration: 0.001ms !important; animation-iteration-count: 1 !important; transition-duration: 0.001ms 
!important; scroll-behavior: auto !important; }
}

/* Header */
.site-header { position: sticky; top: 0; z-index: 10; background: rgba(255,255,255,.8); backdrop-filter: blur(8px); 
border-bottom: 1px solid #eef3f7; }
.site-header__nav { display: flex; align-items: center; justify-content: space-between; height: 64px; }
.site-header__logo { display: flex; align-items: center; gap: .75rem; font-weight: 700; color: var(--pc-dark); }
.site-header__links a { position: relative; margin-left: 1rem; font-weight: 600; color: var(--pc-text); 
text-decoration: none; }
.site-header__links a::after { content: ''; position: absolute; left: 0; right: 100%; bottom: -6px; height: 2px; 
background: var(--pc-blue); transition: right var(--motion-duration-fast) var(--motion-ease); }
.site-header__links a:hover::after, .site-header__links a.active::after { right: 0; }
.site-header__links a.active { color: var(--pc-blue); }

/* Skip link */
.skip-link { position: absolute; left: -999px; top: -999px; background: #000; color: #fff; padding: .5rem .75rem; 
border-radius: 6px; }
.skip-link:focus { left: 1rem; top: 1rem; z-index: 100; }

/* Footer */
.site-footer { padding: 2rem 0; color: var(--pc-text-light); background: #0c1d3608; border-top: 1px solid #eef3f7; }
.site-footer__grid { display: grid; gap: 1rem; grid-template-columns: 1fr; }
@media (min-width: 800px) { .site-footer__grid { grid-template-columns: 2fr 1fr 1fr; } }

/* Hero */
.hero {
  position: relative; padding: 5rem 0 3rem; overflow: hidden; color: #fff;
  background: radial-gradient(1800px 600px at 80% -10%, #3a6ea5 10%, transparent 60%) , linear-gradient(120deg, 
#0c1d36 0%, #112a4f 100%);
}
.hero__inner { display: grid; gap: 2rem; align-items: center; }
@media (min-width: 900px) { .hero__inner { grid-template-columns: 1.2fr 1fr; } }
.hero h1 { color: #fff; font-weight: 700; font-size: clamp(2rem, 3.2vw + 1rem, 3rem); }
.hero p { color: #e8f1f7; font-size: 1.1rem; }
.hero__badges { display: flex; gap: .75rem; flex-wrap: wrap; }
.badge { background: #ffffff14; border: 1px solid #ffffff26; color: #e8f1f7; padding: .35rem .65rem; border-radius: 
999px; font-weight: 600; }

/* Pricing */
.pricing { display: grid; gap: 1.25rem; grid-template-columns: repeat(auto-fit,minmax(240px,1fr)); }
.price-card { background: #fff; border-radius: var(--radius); padding: 1.25rem; box-shadow: var(--shadow-sm); border: 
1px solid #e7eef4; }
.price-card__title { font-weight: 700; }
.price-card__price { font-weight: 700; color: var(--pc-blue); font-size: 1.8rem; }

/* Before/After */
.ba { position: relative; border-radius: var(--radius); overflow: hidden; box-shadow: var(--shadow-sm); }
.ba__img { display: block; width: 100%; height: auto; }
.ba__overlay { position: absolute; inset: 0; overflow: hidden; }
.ba__handle { position: absolute; top: 50%; transform: translateY(-50%); width: 2px; background: #fff; box-shadow: 0 0 
0 2px rgba(0,0,0,.1); left: 50%; }
.ba__slider { position: absolute; left: 0; right: 0; bottom: 1rem; width: 60%; margin: 0 auto; }

