+} else {
+  $feDir = Join-Path $root 'apps/frontend'
+  Write-Host 'Opening http://localhost:4200' -ForegroundColor Green
+  Start-Process -FilePath 'ng' -ArgumentList @('serve','-o') -WorkingDirectory $feDir
+}
+
+Write-Host "Done. API job: 'Get-Job -Name paderclean-api' (Stop-Job/Receive-Job as needed)." -ForegroundColor Green
+
*** End Patch"]}

