payload.output:
{"output":"param()
$ErrorActionPreference = 'Stop'

function Write-Section($text) { Write-Host "`n=== $text ===`n" -ForegroundColor Cyan }
function Invoke-Bypass([string]$path, [string[]]$args=@()) {
  # Runs a PowerShell script with ExecutionPolicy Bypass so it works on locked-down machines
  & powershell -NoProfile -ExecutionPolicy Bypass -File $path @args
}

try {

# Resolve repo root
$root = Split-Path -Parent $PSScriptRoot

  # 1) Ensure API env and start API
  Write-Section 'Setting up API (apps/api)'
  $apiDir = Join-Path $root 'apps/api'
  if (-not (Test-Path $apiDir)) { throw "Directory not found: $apiDir" }

  $apiEnv = Join-Path $apiDir '.env'
  $apiEnvExample = Join-Path $apiDir '.env.example'
  if (-not (Test-Path $apiEnv) -and (Test-Path $apiEnvExample)) {
    Copy-Item -Force $apiEnvExample $apiEnv
    Write-Host 'Created apps/api/.env from .env.example' -ForegroundColor Green
  }

  Push-Location $apiDir
  try {
    if (-not (Test-Path 'node_modules')) {
      Write-Host 'Installing API dependencies...' -ForegroundColor Yellow
      npm i | Out-Host
    }
  } finally { Pop-Location }

  # Start API in background job
  if (-not (Get-Job -Name 'paderclean-api' -State Running -ErrorAction SilentlyContinue)) {
    Write-Host 'Starting API (background job)...' -ForegroundColor Yellow
    $null = Start-Job -Name 'paderclean-api' -ScriptBlock {
      $api = Join-Path $using:root 'apps/api'
      Set-Location $api
      npm run dev
    }
  }

  # 2) Scaffold + augment frontend
  Write-Section 'Setting up Frontend (apps/frontend)'
  Push-Location $root
  try {
    if (-not (Test-Path 'apps/frontend/angular.json')) {
      Write-Host 'Scaffolding Angular app...' -ForegroundColor Yellow
      Invoke-Bypass (Join-Path $root 'tools/scaffold-frontend.ps1')
    } else {
      Write-Host 'Angular app already scaffolded. Skipping scaffold.' -ForegroundColor DarkGray
    }
    Write-Host 'Augmenting frontend (templates, SEO, assets, manifest)...' -ForegroundColor Yellow
    Invoke-Bypass (Join-Path $root 'tools/augment-frontend.ps1')
  } finally { Pop-Location }

  # 3) Start Angular dev server (blocking)
  Write-Section 'Starting SPA (Angular dev server)'
  $feDir = Join-Path $root 'apps/frontend'
  if (Get-Command ng -ErrorAction SilentlyContinue) {
    Write-Host 'Opening http://localhost:4200' -ForegroundColor Green
    Push-Location $feDir
    try { & ng serve -o } finally { Pop-Location }
  }
  elseif (Get-Command npx -ErrorAction SilentlyContinue) {
    Write-Warning 'Angular CLI (ng) not found, using npx @angular/cli to run the dev server...'
    Write-Host 'Opening http://localhost:4200' -ForegroundColor Green
    Push-Location $feDir
    try { & npx @angular/cli@^20 serve -o } finally { Pop-Location }
  }
  else {
    Write-Warning 'Neither ng nor npx found. Please install Angular CLI: npm i -g @angular/cli@^20'
    Write-Host "API is running as a background job. View with: Get-Job -Name paderclean-api" -ForegroundColor Yellow
    Read-Host 'Press Enter to exit'
  }

  Write-Host "Done. API job: 'Get-Job -Name paderclean-api' (Stop-Job/Receive-Job as needed)." -ForegroundColor Green
}
catch {
  Write-Host "`nERROR: $($_.Exception.Message)" -ForegroundColor Red
  if ($_.ScriptStackTrace) { Write-Host $($_.ScriptStackTrace) -ForegroundColor DarkRed }
  Write-Host 'Press Enter to exit...' -ForegroundColor Yellow
  Read-Host | Out-Null
  exit 1
}

